function toggleAnswer(questionElement){const answerElement=questionElement.nextElementSibling;const arrowElement=questionElement.querySelector('.arrow');const isExpanded=questionElement.parentElement.getAttribute('aria-expanded')==='true';const modalQuestion=document.getElementById("modalQuestion");const modalAnswer=document.getElementById("modalAnswer");modalQuestion.textContent=questionElement.querySelector('h6').textContent;modalAnswer.textContent=isExpanded?'':answerElement.querySelector('p').textContent;if(isExpanded){closeModal();arrowElement.textContent='+';}else{document.getElementById("faqModal").style.display="block";}
questionElement.parentElement.setAttribute('aria-expanded',!isExpanded);arrowElement.textContent=isExpanded?'+':'-';}
function closeModal(){document.getElementById("faqModal").style.display="none";const faqQuestions=document.querySelectorAll('.faq-question');faqQuestions.forEach(question=>{question.querySelector('.arrow').textContent='+';});}
document.querySelector('.close').onclick=function(){closeModal();}
window.onclick=function(event){const modal=document.getElementById("faqModal");if(event.target===modal){closeModal();}}
document.addEventListener("DOMContentLoaded",function(){const searchQuery=new URLSearchParams(window.location.search).get('q');if(searchQuery){const faqItems=document.querySelectorAll('.faq-item');faqItems.forEach(item=>{const questionText=item.querySelector('.faq-question h6').textContent.toLowerCase();if(questionText.includes(searchQuery.toLowerCase())){item.scrollIntoView({behavior:'smooth',block:'start'});item.classList.add('highlight');}});}
const faqItems=document.querySelectorAll('.faq-item');faqItems.forEach((item,index)=>{setTimeout(()=>{item.classList.add('visible');},index*100);});});;