function navigate(direction){const currentPage=new URLSearchParams(window.location.search).get('page')||1;const nextPage=parseInt(currentPage)+direction;const newsItems=document.querySelectorAll('.news-item');newsItems.forEach(item=>{item.classList.remove('visible');});const buttons=document.querySelectorAll('.pagination-buttons button');buttons.forEach(button=>{button.classList.add('color-transition');});setTimeout(()=>{window.location.href=`?page=${nextPage}`;},500);}
function scrollToNews(){const hash=window.location.hash;if(hash){const target=document.querySelector(hash);if(target){setTimeout(()=>{target.scrollIntoView({behavior:"smooth",block:"start"});},700);}}}
window.onload=()=>{const newsItems=document.querySelectorAll('.news-item');newsItems.forEach(item=>{item.classList.add('visible');});scrollToNews();};if(window.location.hash){scrollToNews();}
function showFullText(newsId,fullText){document.getElementById('modalContent').innerText="Новость";document.getElementById('modalOverlay').style.display='block';document.getElementById('fullTextModal').style.display='block';}
function closeModal(){document.getElementById('modalOverlay').style.display='none';document.getElementById('fullTextModal').style.display='none';};