(function(){emailjs.init("cFdxvAOZutfnLnRyJ");})();function sendEmail(event){event.preventDefault();const form=document.getElementById("contact-form");const sendButton=document.getElementById('sendButton');sendButton.disabled=true;const recaptchaResponse=grecaptcha.getResponse();if(recaptchaResponse.length===0){alert("Пожалуйста, подтвердите, что вы не робот.");sendButton.disabled=false;return;}
const formData={name:form.elements["name"].value,email:form.elements["email"].value,phone:form.elements["phone"].value,message:form.elements["question"].value,recaptcha_response:recaptchaResponse};emailjs.send("service_ff5uksc","template_qo1cyv8",formData).then(response=>{alert("Сообщение успешно отправлено!");form.reset();grecaptcha.reset();}).catch(error=>{console.error("Ошибка при отправке:",error);alert("Не удалось отправить сообщение. Попробуйте позже.");}).finally(()=>{sendButton.disabled=false;});}
